targets.expr:
count(
        sum(
          min_over_time(
            kube_pod_labels{
              pod=~"^$jhubNameLabel.*"
            }[30d]
          )
        ) by (pod)
      )

count(
        sum(
          min_over_time(
            kube_pod_labels{
              pod=~"^$jhubNameLabel.*"
            }[1d]
          )
        ) by (pod)
      )

( max( # Ideally we just want 'current', but max will do. This metric is a gauge, so sum is inappropriate
        container_memory_working_set_bytes{pod=~"^$jhubNameLabel.*"}
      ) by (namespace, pod)
      /
      sum(
        kube_pod_container_resource_limits{pod=~"^$jhubNameLabel.*", resource="memory"}
      ) by (namespace, pod)) > 0.8
